<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Team extends MY_Controller {    public $tmpl_path;    public $sec_path;    public $img_arr;    public function __construct() {        parent::__construct();        $this->load_modelslib();        $this->init_vars();        $this->load->helper('text');    }    public function load_modelslib() {        $this->load->model('M_team');    }    public function init_vars() {        $this->tmpl_path = 'admin/layout/template';        $this->sec_path = 'admin/team';        $this->redir_path = 'sitemanager/team';        $this->imagepath = 'assets/images/team';        $this->controller = 'sitemanager/team';        $this->title = 'Team';        $this->logname = 'team';    }    public function index() {        $this->has_access(); // Checking admin authentication -> core/MY_Controller.php        $nav_links = "<li><a href='#'>$this->title</a></li>";        $hrdata = array(            'title' => $this->title,            'sub_title' => $this->title,            'plus_link' => "$this->controller/add",            'controller' => $this->controller,            'imagepath' => $this->imagepath,        );        $content = array();        $total_row = $this->M_team->recordCount(); // Returns total number of records        $this->config->load('pagination');        $config["total_rows"] = $total_row;        $config["per_page"] = 25;        $config["base_url"] = base_url() . '/' . $this->redir_path;        $page = ($this->uri->segment(3)) ? $this->uri->segment(3) : 0;        $this->pagination->initialize($config);        $data = $this->template->set_template_admin($this->adm_name, $hrdata, $nav_links, '');        $content['result'] = $this->M_team->read($config["per_page"], $page);        $content['page'] = $page;        $content['pagination'] = $this->pagination->create_links();        $content['hrdata'] = $hrdata;        $data['content'] = $this->load->view($this->sec_path . '/index.php', $content, true);        $this->load->view($this->tmpl_path, $data);    }    public function add() {        $this->has_access();        $content = array();        $nav_links = "<li><a href='" . base_url($this->controller) . "'>$this->title </a></li>        <li class='active'>Add</li>";                $hrdata = array(            'title' => $this->title,            'sub_title' => 'Add ' . $this->title,            'back_link' => $this->controller,            'controller' => $this->controller,            'imagepath' => $this->imagepath,        );        $this->form_validation->set_rules('name', 'Name', 'trim|required');        $this->form_validation->set_rules('venue', 'Venue', 'trim|required');        $this->form_validation->set_rules('address', 'Address', 'trim|required');        $this->form_validation->set_rules('status', 'status', 'trim|required');        if (isset($_FILES['image']['name']) && $_FILES['image']['name'] == '') {            $this->form_validation->set_rules('image', 'Image', 'required');        }        if ($this->form_validation->run() != false) {            $img_upload = '';            if (!empty($_FILES['image']['name'])) {                $img_upload = $this->image_upload($_FILES['image']['name']);            }            $insert_data = array(                'name' => $this->input->post('name'),                'venue' => $this->input->post('venue'),                'image' => $img_upload,                'address' => $this->input->post('address'),                'status' => $this->input->post('status'),            );            $insert = $this->M_team->insert($insert_data);            $this->M_log->insert_log("add_$this->logname", "Admin Added $this->title", $this->adm_id, $insert);            $this->M_notify->set_notification("$this->title Added Successully", 'success');            redirect($this->redir_path);        }        $data = $this->template->set_template_admin($this->adm_name, $hrdata, $nav_links, '');        $data['content'] = $this->load->view($this->sec_path . '/add.php', $sdata = array('hrdata' => $hrdata), true);        $this->load->view($this->tmpl_path, $data);    }    public function edit($id) {                $this->has_access();        $content = array();        $content['editdata'] = $this->M_team->getRow($id);        if (!$id > 0 || empty($content['editdata'])) {            $this->M_notify->set_notification('Requested Content not found', 'error');            redirect($this->redir_path);            die();        }        $nav_links = "<li><a href='#'> $this->title </a></li>        <li class='active'>Edit</li>";        $hrdata = array(            'title' => $this->title,            'sub_title' => 'Edit ' . $this->title,            'back_link' => $this->controller,            'controller' => $this->controller,            'imagepath' => $this->imagepath,        );        $this->form_validation->set_rules('name', 'Name', 'trim|required');        $this->form_validation->set_rules('venue', 'Venue', 'trim|required');        $this->form_validation->set_rules('address', 'Address', 'trim|required');        $this->form_validation->set_rules('status', 'status', 'trim|required');        if ($this->form_validation->run() != false) {            $update_data = array(                'name' => $this->input->post('name'),                'venue' => $this->input->post('venue'),                'address' => $this->input->post('address'),                'status' => $this->input->post('status'),            );            if (!empty($_FILES['image']['name'])) {                $img_upload = $this->image_upload($_FILES['image']['name']);                $this->M_team->delImg($this->imagepath, $id);                $update_data['image'] = $img_upload;            }            $update = $this->M_team->update($update_data, $id);            $this->M_log->insert_log("upd_$this->logname", "Admin Updated $this->title", $this->adm_id, $id);            $this->M_notify->set_notification("$this->title Updated Successully", 'success');            redirect($this->redir_path);        }        $data = $this->template->set_template_admin($this->adm_name, $hrdata, $nav_links, '');        $content['hrdata'] = $hrdata;        $data['content'] = $this->load->view($this->sec_path . '/edit.php', $content, true);        $this->load->view($this->tmpl_path, $data);    }        public function image_upload($image, $image_field = 'image') {        $img_ext = explode('.', $image);        $img_upl_name = 'p' . get_rand_number() . '.' . end($img_ext);        $upl_path = $this->imagepath;         $img_full_path = $upl_path . $img_upl_name;        $mksubdir = $this->imagepath;        if (!is_dir($mksubdir)) {            mkdir($mksubdir);        }        $config['upload_path'] = $upl_path;        $config['allowed_types'] = 'jpg|jpeg|png|gif';        $config['file_name'] = $img_upl_name;        //Load upload library and initialize configuration        $this->load->library('upload', $config);        $this->upload->initialize($config);        if ($this->upload->do_upload($image_field)) {            return $img_upl_name;        }    }    public function delimg($id = 0) {        $this->has_access();        $del_img = $this->M_team->delImg($this->imagepath, $id);        $this->M_notify->set_notification('Image deleted successfully', 'success');        redirect($this->redir_path . '/edit/' . $id);    }    public function delete($id) {        $this->has_access();        if (!$id > 0) {            $this->M_notify->set_notification('Requested Content not found', 'error');            redirect($this->redir_path);            die();        }        $this->M_team->delete($this->imagepath, $id);        $this->M_log->insert_log("del_$this->logname", "Admin Deleted $this->title", $this->adm_id, $id);        $this->M_notify->set_notification("$this->title deleted successfully", 'success');        redirect($this->redir_path);    }}