<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class M_match extends CI_Model {    public $tblname;    public function __construct() {        parent::__construct();        $this->tblname = 'match';    }    public function read($limit = NULL, $offset = NULL) {        $fields = 'match.*, H.name as home, W.name as winner_team, A.name as away ';        $this->db->select($fields);        $this->db->from($this->tblname);        $this->db->order_by("date", "asc");        $this->db->join('team H', "match.home_team = H.id and H.status='Y'",'left');        $this->db->join('team A', "match.away_team = A.id and A.status='Y'",'left');        $this->db->join('team W', "match.winner = W.id and W.status='Y'",'left');        $this->db->limit($limit, $offset);        $query = $this->db->get();        return $query->result();    }    public function recordCount() {        $this->db->from($this->tblname);        $count = $this->db->count_all_results();        return $count;    }    public function insert($data) {        $insert = $this->db->insert($this->tblname, $data);        if ($this->db->affected_rows() > 0) {            return $this->db->insert_id();        }        return false;    }    public function getRow($id) {        $fields = 'match.*, H.name as home, W.name as winner_team, A.name as away';        $this->db->select($fields);        $this->db->where(array($this->tblname.'.id' => $id));        $this->db->join('team H', "match.home_team = H.id and H.status='Y'",'left');        $this->db->join('team A', "match.away_team = A.id and A.status='Y'",'left');        $this->db->join('team W', "match.winner = W.id and W.status='Y'",'left');        $this->db->from($this->tblname);        $query = $this->db->get();        return $query->row();    }    public function update($data, $id) {        $this->db->where('id', $id);        $this->db->update($this->tblname, $data);        if ($this->db->affected_rows() > 0) {            return true;        }        return $this->db->last_query();    }    public function delete($path, $id = 0) {        $this->db->where('id', $id);        $this->db->delete($this->tblname);        return true;    }    }