<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class M_player extends CI_Model {    public $tblname;    public function __construct() {        parent::__construct();        $this->tblname = 'player';    }    public function read($limit = NULL, $offset = NULL) {        $fields = 'player.*, team.name as team_name';        $this->db->select($fields);        $this->db->from($this->tblname);        $this->db->join('team', "player.team = team.id and team.status='Y'",'left');        $this->db->order_by("name", "asc");        $this->db->limit($limit, $offset);        $query = $this->db->get();        return $query->result();    }    public function recordCount() {        $this->db->from($this->tblname);        $count = $this->db->count_all_results();        return $count;    }    public function insert($data) {        $insert = $this->db->insert($this->tblname, $data);        if ($this->db->affected_rows() > 0) {            return $this->db->insert_id();        }        return false;    }    public function getRow($id) {        $fields = '*';        $this->db->select($fields);        $this->db->where(array('id' => $id));        $this->db->from($this->tblname);        $query = $this->db->get();        return $query->row();    }    public function update($data, $id) {        $this->db->where('id', $id);        $this->db->update($this->tblname, $data);        if ($this->db->affected_rows() > 0) {            return true;        }        return $this->db->last_query();    }    public function delete($path, $id = 0) {        $this->delImg($path, $id);        $this->db->where('id', $id);        $this->db->delete($this->tblname);        return true;    }    public function delImg($path, $id) {        $this->db->select('image');        $this->db->where('id', $id);        $this->db->from($this->tblname);        $query = $this->db->get();        $result = $query->row();        $unlink_img = $path . "/" . $result->image;        if ($result->image != '') {            unlink($unlink_img);        }        $this->db->where('id', $id);        $this->db->update($this->tblname, array('image' => ''));        return $result->image;    }    }