<div class="panel panel-flat">    <div class="panel-heading">            </div>    <div class="panel-body">        <?php        $attributes = array('class' => 'form-horizontal', 'id' => 'myform', 'name' => 'frm_client');        echo form_open_multipart($hrdata['controller'] .'/edit/' . $editdata->id, $attributes);        ?>        <div class="form-group">                <label class="control-label col-lg-2">Title<span class="required"> *</span></label>                <div class="col-lg-6">                                        <input class="form-control" id="title" value="<?php echo set_value('title', $editdata->title); ?>" name="title" type="text" placeholder=" Title"  />                    <div class="error error-txt error-name"></div>                    <?php echo form_error('title'); ?>                                    </div>        </div>        <div class="form-group">                <label class="control-label col-lg-2">Date<span class="required"> *</span></label>                <div class="col-lg-6">                    <div class="input-group">                    <span class="input-group-addon"><i class="icon-calendar"></i></span>                    <input class="form-control datepicker-format" id="date" value="<?php echo set_value('date', date('m/d/Y',strtotime($editdata->date))); ?>" name="date" type="text" placeholder=" Date"  />                    </div>                    <div class="error error-txt error-date"></div>                    <?php echo form_error('date'); ?>                                    </div>        </div>        <div class="form-group">                <label class="control-label col-lg-2">Image</label>                <div class="col-lg-6">                    <?php if (isset($editdata->image) && $editdata->image != '') { ?>                        <img src="<?php echo base_url().'/'.$hrdata['imagepath'].'/' ?><?php echo $editdata->image ?>" width="120" class="left-float mr-10p" alt="icon">                    <?php } ?>                        <div id="img-name"></div>                        <div style="margin: 5px 0"><label>Change                                 <input class="file-styled hidden" type="file" name="image" id="image" onchange="display_name()" ></label>                    </div>                                        <span style="font-size: 10px">Allowed types: JPEG,JPG,PNG  Max-size:2MB</span><br/>                     <div class="error error-img error-txt"></div>                    <?php echo form_error('image'); ?>                                    </div>        </div>        <div class="form-group">                <label class="control-label col-lg-2">Description<span class="required"> *</span></label>                <div class="col-lg-8">                                        <textarea name="description" id="editor" class="summernote" ><?php echo set_value('description',$editdata->description);?></textarea>                                        <div class="error error-editor error-txt"></div>                    <?php echo form_error('description'); ?>                                    </div>        </div>        <div class="form-group">                <label class="control-label col-lg-2">Status<span class="required"> *</span></label>                <div class="col-lg-6">                    <?php $status = array('Y' => 'Active', 'N' => 'Inactive'); ?>                    <?php echo form_dropdown('status', $status, set_value('status',$editdata->status), 'class="dropdown form-control" id="status"'); ?>                    <?php echo form_error('status'); ?>                                    </div>        </div>        <div class="form-group">                <label class="control-label col-lg-2">Show in home page<span class="required"> *</span></label>                <div class="col-lg-6">                    <?php $show = array('Y' => 'Yes', 'N' => 'No'); ?>                    <?php echo form_dropdown('show', $show, set_value('show',$editdata->show_in_home_page), 'class="dropdown form-control" id="show"'); ?>                    <?php echo form_error('show'); ?>                                    </div>        </div>        <div class="form-group">            <label class="control-label col-lg-2">&nbsp;</label>                <div class="col-lg-4"><button type="submit" name="submit" class="btn btn-primary" onclick="return validate_form();"  >Submit</button></div>        </div>        <?php form_close(); ?>    </div> </div><script type="text/javascript">    function display_name(){        var fuData=document.getElementById('image');        var fu1 = document.getElementById('image').value;        fu1=fu1.substr(fu1.lastIndexOf('\\') + 1)        $('#img-name').html(fu1);    }    function validate_form()    {               var thisform = document.frm_client;        var fuData=document.getElementById('image');        var fu1 = document.getElementById('image').value;        var name = document.getElementById('title').value;        var date = document.getElementById('date').value;        var editor = document.getElementById('editor').value;        // var editor= tinyMCE.activeEditor.getContent();        var ext = fu1.substr(fu1.lastIndexOf('.') + 1).toLowerCase();        editor=$(editor).text();                $('.error').html('');        if(name.trim() == ""){            $('.error-name').html('<p >Please enter a valid title.</p>');            return false;        }        if(date.trim() == ""){            $('.error-date').html('<p >Please enter a valid date.</p>');            return false;        }        if (fu1 != "")        {            if (ext != 'jpg' && ext != 'png' && ext != 'jpeg' && ext != 'gif')            {                $('.error-img').html('<p >Upload jpg/png images only</p>');                               return false;            }            if(fuData.files && fuData.files[0]){                    var size=fuData.files[0].size;                    if(size > 2400000){                        $('.error-img').html('<p>Please upload a jpg/png image less than 2MB </p>');                                         return false;                 }             }                    }         if(editor.trim() == ""){            $('.error-editor').html('<p >Please enter a valid description.</p>');            return false;        }        thisform.submit();    }</script>			       