<div class="panel panel-flat">    <div class="panel-heading">            </div>    <div class="panel-body">        <?php        $attributes = array('class' => 'form-horizontal', 'id' => 'myform', 'name' => 'frm_client');        echo form_open_multipart($hrdata['controller'] .'/reset_password/' . $editdata->id, $attributes);        ?>                <div class="form-group">                <label class="control-label col-lg-2">Password<span class="required"> *</span></label>                <div class="col-lg-6">                                        <input class="form-control" id="password" value="<?php echo set_value('password'); ?>" name="password" type="password" placeholder="Password"  />                    <div class="error error-txt error-password"></div>                    <?php echo form_error('password'); ?>                                    </div>        </div>        <div class="form-group">                <label class="control-label col-lg-2">Confirm Password<span class="required"> *</span></label>                <div class="col-lg-6">                                        <input class="form-control" id="conpassword" value="<?php echo set_value('conpassword'); ?>" name="conpassword" type="password" placeholder="Confirm Password"  />                    <div class="error error-txt error-conpassword"></div>                    <?php echo form_error('conpassword'); ?>                                    </div>        </div>                <div class="form-group">            <label class="control-label col-lg-2">&nbsp;</label>                <div class="col-lg-4"><button type="submit" name="submit" class="btn btn-primary" onclick="return validate_form();"  >Submit</button></div>        </div>        <?php form_close(); ?>    </div> </div><script type="text/javascript">        function validate_form()    {               var thisform = document.frm_client;        var password = document.getElementById('password').value;        var conpassword = document.getElementById('conpassword').value;        var passwordlength=6;        // var editor= tinyMCE.activeEditor.getContent();         // editor=$(editor).text();                $('.error').html('');        if(password.trim() == "" || password.length < passwordlength ){            $('.error-password').html('<p >Password must contain atleast '+passwordlength+' characters .</p>');            return false;        }        if(conpassword.trim() == "" || conpassword.length < passwordlength ){            $('.error-conpassword').html('<p >Confirm Password must contain atleast '+passwordlength+' characters .</p>');            return false;        }        if(conpassword!=password){            $('.error-conpassword').html('<p >Password donot match.</p>');            return false;        }                thisform.submit();    }    function validateEmail(emailID)    {        var atpos = emailID.indexOf("@");        var dotpos = emailID.lastIndexOf(".");        if (atpos < 1 || (dotpos - atpos < 2))        {            return false;        }        return(true);    }</script>			